import './App.css';
import $ from 'jquery';
import Header from './component/Header';
import Body from './component/Body';
import Footer from './component/Footer';
import Mode from './component/Mode';
import { ModeProvider } from './component/ModeContext';
import { ModeContext } from './component/ModeContext';
import { Desktop } from './component/Mobile';
import { Mobile } from './component/Mobile';
import { useContext, useState } from 'react';
import { LanguageContext, LanguageProvider } from './component/LanguageContext';
function App() {
  const values = [true, 'sm-down', 'md-down', 'lg-down', 'xl-down', 'xxl-down'];
  const [fullscreen, setFullscreen] = useState(true);
  //const [show, setShow] = useState(false);
  const [show,setShow, language, setLanguage, mobile, setMobile] = useContext(ModeContext);

  function handleShow() {
    setFullscreen(0);
    setShow(true);
  }
  $(document).ready(function () {
/*     function isVisible($el) {
      let docViewTop = $(window).scrollTop();
      let docViewBottom = docViewTop + $(window).height();
      let elTop = $el.offset().top;
      let elBottom = elTop + $el.height();
      return ((elBottom <= docViewBottom) && (elTop >= docViewTop));
    } */

    $('.ServicesImg').hide();
    $('.ServicesP').hide();
    $(window).scroll(function () {
      // cheek if it is Mobile or desktop



      if ($('.Services').offset().top - $(window).scrollTop() < 400) {
        $('.ServicesImg').show(500);
        $('.ServicesP').fadeIn(3000);

      }
      if ($('.Services').offset().top - $(window).scrollTop() > 400) {
        $('.ServicesImg').hide(500);
        $('.ServicesP').hide(500);
      }
    });

  });

  return (<div >
    <ModeProvider>
      <Mode />
      <Header className='header' />
      <Body />
      <Footer />
    </ModeProvider>

  </div>

  );
}


export default App;
