import { useContext } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { ModeContext } from "./ModeContext";
import $ from 'jquery';
function Contact() {
  const [show, setShow, language, setLanguage, mobile] = useContext(ModeContext);
  const sty = { height: mobile.contact };

  $(document).ready(function () {
    $('#c_sub').click(function () {
      let name = $('#c_name').val(),
        phone = $('#c_phone').val(),
        email = $('#c_email').val(),
        text = $('#c_text').val();
      $.ajax({
        url: 'http://jazeera.openewz.com/php/add_contact.php',
        type: 'post',
        data: { name: name, phone: phone, email: email, text: text },
        beforeSend: function () {

        },
        success: function (data) {
          alert("Thank you. We will back to you.");
        },
        error: function (error) {
          alert("Check your internet connection please. or call us on phone number.");
        }
      });
    });
  });

  return (
    <div className="Contact" id="contact" style={sty}>
      <br />
      <center><u><h2 style={{ color: 'white' }}>{language.contact.title}</h2></u>
        <hr style={{ color: 'white', width: '100px', height: '2px' }} />
      </center>

      <Container >
        <br /><br />
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <center>

          <Row >
            <Col xs={12} md={10} >
              <Form className="ContactForm" style={{ width:'100%'}} >
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                  <Form.Control type="email" id="c_email" placeholder="email@example.com" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                  <Form.Control type="tel" id="c_phone" placeholder="tel: +971501234567" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                  <Form.Control type="text" id="c_name" placeholder="Your full name" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                  <Form.Control id="c_text" as="textarea" rows={3} />
                </Form.Group>
                <Button id="c_sub">{language.contact.button}</Button>
              </Form>
            </Col>
            <Col xs={12} md={2} style={{padding:'20px'}}>
              <Row >
                <Col xs={2} md={12}>
                <a href="https://wa.me/message/BKS6ZRLMIBNJG1"><img src="../img/whatsapp.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={2} md={12}>
                <a href="https://www.facebook.com/ركن-الجزيرة-للاثاث-rukn-al-jazeerah-furniture-105254325361815"><img src="../img/facebook.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={3} md={12}>
                  <img src="../img/twitter.png" className="ContactIcon" />
                </Col>
                <Col xs={2} md={12}>
                <a href="https://www.instagram.com/rukn_al_jazeerah/"><img src="../img/instagram.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={2} md={12}>
                <a href="tel: +971563201418"><img src="../img/phone-call.png" className="ContactIcon" /></a>
                </Col>
              </Row>
            </Col>
          </Row>
        </center>

      </Container>
    </div>
  );
}
export default Contact;
