import React, { createContext, useState } from "react";
import Body from "./Body";
import Footer from "./Footer";
import Header from "./Header";
import { English } from "./Language";
import { Desktop, Mobile } from "./Mobile";


export const ModeContext = createContext();

export const ModeProvider = props => {
  const [fullscreen, setFullscreen] = useState(true);
  const [show, setShow] = useState(false);
  const [language, setLanguage] = useState(English);
  const [mobile, setMobile] = useState(Desktop);

  return (
    <ModeContext.Provider value={[show, setShow, language, setLanguage, mobile, setMobile]}>
      <Header />
      <Body />
      <Footer />
    </ModeContext.Provider>
  );
};
