import { Container, Row, Col, Button } from "react-bootstrap";
import React, {useContext} from "react";
import { ModeContext } from "./ModeContext";
function Services() {
  const [show, setShow, language, setLanguage,mobile]= useContext(ModeContext);
  const sty = {height:mobile.service};
  function handleShow(){
    setShow(true);
  }
  return (
    <div className="Services" id="services" style={sty}>
      <br />
      <center><u><h2 style={{ color: 'white' }}>{language.service.title}</h2></u>
        <hr style={{ color: 'white', width: '100px', height: '2px' }} />
      </center>

      <Container>
        <br /><br />
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <Row >
          <Col xs={12} md={6} >
            <center>
              <img src="./img/2.jpg" className="ServicesImg" />
            </center>
          </Col>
          <Col xs={12} md={6} style={{padding:'20px'}} >
            <center>
              <p className="ServicesP" style={{direction:'rtl', color:'white' }}>
                <b>
                {language.service[1]}
                </b>
                <br />
                <b>
                {language.service[2]}
                </b>
                <br />
                <b>
                {language.service[3]}
                </b>
                <br />
                <b>
                {language.service[4]}
                </b>
                <br />
                <b>
                {language.service[5]}
                </b>
                <br />
                <b>
                {language.service[6]}
                </b>
                <br />
                <b>
                {language.service[7]}
                </b>
                <br />
                <b>
                {language.service[8]}
                </b>
                <br />
                <b>
                {language.service[9]}
                </b>
                <br /><br />
                <b>
                <Button onClick={handleShow} style={{backgroundColor:'green'}} >{language.service.button}</Button>
                </b>
              </p>
            </center>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
export default Services;
