import { useContext } from "react";
import { Container, Row, Col } from "react-bootstrap";
import IServicesItem from "./IServicesItem";
import { ModeContext } from "./ModeContext";
function About() {
  const [show, setShow, language, setLanguage, mobile]= useContext(ModeContext);
  const sty = {textAlign:'justify',direction:language.footer.dir}; 
  const sty2 = {height:mobile.about};
  return (
    <div id="about" className="About"  style={sty2}>
      <br />
      <center><u><h2 style={{color:'white'}}>{language.about.title}</h2></u>
      <hr style={{color:'white', width:'100px', height:'2px'}} />
      </center>

      <Container>
        <br /><br />
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <Row >
          <Col xs={12} md={6} style={{paddingBottom:'20px'}}>
            <center>
              <img src="./img/logo.jpeg" className="ServicesImg" />
            </center>
          </Col>
          <Col xs={12} md={6}  >
            <center>
              <p className="ServicesP" style={sty}>
                {language.about.body}
              </p>
            </center>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
export default About;
