          return ((elBottom <= docViewBottom) && (elTop >= docViewTop));
        } */

    $(window).scroll(function () {
      // cheek if it is Mobile or desktop
      if (!(window.innerWidth <= 800) && !(window.innerHeight <= 600)) {
        setMobile(Desktop);
        $(".Calling").hide(500);
        $(".Whatsapping").hide(500);
      } else {
        setMobile(Mobile);
        $(".Calling").show(500);
        $(".Whatsapping").show(500);
      }
    });
  });

  return (
    <div>
      <Mode />
      <SlideShow />
      <About />
      <IServices />
      <Services />
      <OldNew />
      <About />
      <Contact />
      <Map />
