import { Carousel } from "react-bootstrap";
import SlideShow from "./SlideShow";
import IServices from "./IServices";
import Services from "./Services";
import OldNew from "./OldNew";
import About from "./About";
import Contact from "./Contact";
import Map from "./Map";
import { Modal } from 'react-bootstrap';
import Mode from "./Mode";
import { Mobile, Desktop } from "./Mobile";
import { useContext } from "react";
import $ from "jquery";
import { ModeContext } from "./ModeContext";
function Body() {

  const [how, setHow, l, setL, mobile, setMobile] = useContext(ModeContext);

  $(document).ready(function () {
    /*     function isVisible($el) {
          let docViewTop = $(window).scrollTop();
          let docViewBottom = docViewTop + $(window).height();
          let elTop = $el.offset().top;
          let elBottom = elTop + $el.height();
          return ((elBottom <= docViewBottom) && (elTop >= docViewTop));
        } */

    $('.ServicesImg').hide();
    $('.ServicesP').hide();
    $('.SboutImg').hide();
    $('.AboutP').hide();
    $('.OldNewImg3').hide();
    $('.Calling').hide();
    $('.Whatsapping').hide();
    $(window).scroll(function () {
      // cheek if it is Mobile or desktop
      if (!(window.innerWidth <= 800) && !(window.innerHeight <= 600)) {
        setMobile(Desktop);
        $('.Calling').show(500);
        $('.Whatsapping').show(500);
      }
      else {
        setMobile(Mobile);
      }




      if ($('.Services').offset().top - $(window).scrollTop() < 400) {
        $('.ServicesImg').show(500);
        $('.ServicesP').fadeIn(3000);

      }
      if ($('.Services').offset().top - $(window).scrollTop() > 400) {
        $('.ServicesImg').hide(500);
        $('.ServicesP').hide(500);
      }

      if ($('.About').offset().top - $(window).scrollTop() < 400) {
        $('.AboutImg').show(500);
        $('.AboutP').show(3000);

      }
      if ($('.About').offset().top - $(window).scrollTop() > 400) {
        $('.AboutImg').hide(500);
        $('.AboutP').hide(500);
      }



      if ($('.OldNew').offset().top - $(window).scrollTop() < 400) {
        $('.OldNewImg3').show(500);
        $('.OldNewImg3').animate({ top: '-100' }, 1000);
        $('.OldNewImg3').animate({ top: '0' }, 1000);
        $('.OldNewImg2').animate({ width: '80%' }, 5000);
        $('.OldNewImg1').animate({ width: '90%' }, 5000);
        $('.OldNewImg1').animate({ width: '80%' }, 5000);
        $('.OldNewImg2').animate({ width: '90%' }, 5000);


      }
      if ($('.OldNew').offset().top - $(window).scrollTop() > 400) {
        $('.OldNewImg3').hide();
      }


    });
  });

  return (<div >
    <Mode />
    <SlideShow />
    <IServices />
    <Services />
    <OldNew />
    <About />
    <Contact />
    <Map />
    <a href="tel:+971563201418"><img className="Calling" src="../img/calling.gif" /></a>
    <a href="https://wa.me/message/BKS6ZRLMIBNJG1"><img className="Whatsapping" src="../img/whatsapping.gif" /></a>


  </div>

  );
}
export default Body;
