import SlideShow from "./SlideShow";
import IServices from "./IServices";
import Services from "./Services";
import OldNew from "./OldNew";
import About from "./About";
import Contact from "./Contact";
import Map from "./Map";
import { Modal } from 'react-bootstrap';
import Mode from "./Mode";
import { Mobile, Desktop } from "./Mobile";
import { useContext } from "react";
import $ from "jquery";
import { ModeContext } from "./ModeContext";
function Body() {

  const [how, setHow, l, setL, mobile, setMobile] = useContext(ModeContext);

  $(document).ready(function () {
    /*     function isVisible($el) {
          let docViewTop = $(window).scrollTop();
          let docViewBottom = docViewTop + $(window).height();
          let elTop = $el.offset().top;
          let elBottom = elTop + $el.height();
          return ((elBottom <= docViewBottom) && (elTop >= docViewTop));
        } */

    $('.ServicesImg').hide();
    $('.ServicesP').hide();
    $('.OldNewImg1').hide();
      $('.OldNewImg2').hide();
      $('.OldNewImg3').hide();
    $(window).scroll(function () {
      // cheek if it is Mobile or desktop
      if (!(window.innerWidth <= 800) && !(window.innerHeight <= 600)) {
        setMobile(Desktop);
      }
      else {
        setMobile(Mobile);
      }




      if ($('.Services').offset().top - $(window).scrollTop() < 400) {
        $('.ServicesImg').show(500);
        $('.ServicesP').fadeIn(3000);

      }
      if ($('.Services').offset().top - $(window).scrollTop() > 400) {
        $('.ServicesImg').hide(500);
        $('.ServicesP').hide(500);
      }


      if ($('.OldNew').offset().top - $(window).scrollTop() < 400) {
        $('.OldNewImg1').show(500);
        $('.OldNewImg2').show(500);
        $('.OldNewImg3').show(500);
        $('.OldNewImg3').animate({top:200});

      }
      if ($('.OldNew').offset().top - $(window).scrollTop() > 400) {
        $('.OldNewImg1').hide();
        $('.OldNewImg2').hide();
        $('.OldNewImg3').hide();
        $('.OldNewImg3').animate({top:0});
      }


    });
});

return (<div >
  <Mode />
  <SlideShow />
  <IServices />
  <Services />
  <OldNew />
  <About />
  <Contact />
  <Map />


</div>

);
}
export default Body;
