import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { useContext, useState } from "react";
import { ModeContext } from "./ModeContext";
import $ from 'jquery';
function Footer() {
  const [show, setShow, language, setLanguage, mobile] = useContext(ModeContext);
  const sty = { textAlign: language.footer.textAlign, direction: language.footer.dir };
  const sty2 = { height: mobile.footer };

  $(document).ready(function () {
    $('#s_sub').click(function () {
      let email = $('#s_email').val();
      alert(email);
      $.ajax({
        url: 'http://jazeera.openewz.com/php/add_subscrip.php',
        type: 'post',
        data: { email: email},
        beforeSend: function () {

        },
        success: function (data) {
          alert("Thank you. We will back to you.");
        },
        error: function (error) {
          alert("Check your internet connection please. or call us on phone number.");
        }
      });
    });
  });



  return (
    <div className="Footer" style={sty2}>
      <Container >
        <br /><br />
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <Row >
          <Col xs={12} md={4} >
            <center>
              <h5>{language.contact.title}</h5>
              <ul style={{ textAlign: 'left' }}>
                <li>
                  Warehouse 1th Floor,
                </li>
                <li>
                  Street #24, Industrial area #4,
                </li>
                <li>
                  Sharjah, UAE
                </li>
                <li>
                  <a href="tel: +971563201418">+971563201418</a>
                </li>
                <li>
                  rukn.mangemeny@gmail.com
                </li>
              </ul>
              <Row>
                <Col xs={2} md={2}>
                  <a href="https://wa.me/message/BKS6ZRLMIBNJG1"><img src="../img/whatsapp.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={2} md={2}>
                  <a href="https://www.facebook.com/ركن-الجزيرة-للاثاث-rukn-al-jazeerah-furniture-105254325361815"><img src="../img/facebook.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={2} md={2}>
                  <img src="../img/twitter.png" className="ContactIcon" />
                </Col>
                <Col xs={2} md={2}>
                  <a href="https://www.instagram.com/rukn_al_jazeerah/"><img src="../img/instagram.png" className="ContactIcon" /></a>
                </Col>
                <Col xs={2} md={2}>
                  <a href="tel: +971563201418"><img src="../img/phone-call.png" className="ContactIcon" /></a>
                </Col>
              </Row>
            </center>
          </Col>
          <Col xs={12} md={4} style={{padding:'40px'}} >
            <center>
              <h5>{language.service.title}</h5>
              <ul style={sty}>
                <li>
                  {language.service[1]}
                </li>
                <li>
                  {language.service[2]}
                </li>
                <li>
                  {language.service[3]}
                </li>
                <li>
                  {language.service[4]}
                </li>
                <li>
                  {language.service[5]}
                </li>
                <li>
                  {language.service[6]}
                </li>
                <li>
                  {language.service[7]}
                </li>
                <li>
                  {language.service[8]}
                </li>
                <li>
                  {language.service[9]}
                </li>
              </ul>
            </center>
          </Col>
          <Col xs={12} md={4} style={{padding:'20px'}} >
            <center>
              <h5>{language.footer.title}</h5>
              <br />
              <Form className="ContactForm" style={{width:'100%'}}>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                  <Form.Control id="s_email" type="email" placeholder="name@example.com" />
                </Form.Group>
                <Button id="s_sub">{language.contact.button}</Button>
              </Form>
            </center>
          </Col>
        </Row>
      </Container>
    </div>

  );
}

export default Footer;
