import { Form, Button, Modal, Row, Col } from 'react-bootstrap';
import React, { useContext } from 'react';
import { ModeContext } from './ModeContext';
import $ from 'jquery';
function Mode(props) {
  const [show, setShow] = useContext(ModeContext);

  $(document).ready(function () {
    $('#sub').click(function () {
      let name = $('#name').val(),
        phone = $('#phone').val(),
        email = $('#email').val(),
        service = $('#service').val(),
        text = $('#text').val();
      alert(name);
      alert(phone);
      alert(email);
      alert(service);
      alert(text);
      $.ajax({
        url: 'http://localhost:8888/aljazira/public/php/add_order.php',
        type: 'post',
        data: { name: name, phone: phone, email: email, service: service, text: text },
        beforeSend: function () {
          alert('before');
        },
        success: function (data) {
          alert('success');
          alert(data)
        },
        error: function (error) {
          alert('error');
          alert(error);
        }
      });
    });
  });
  return (

    <Modal show={show} onHide={() => setShow(false)} style={{ position: 'fixed', zIndex: '999999999999' }}>
      <Modal.Header closeButton>
        <Modal.Title>Rukn Aljazeera Services Provider:</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form className="ContactForm" style={{ width: '100%' }} onSubmit={sendOrder}>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Select id='service' aria-label="Default select example" required >
              <option>Choose The Services Please:</option>
              <option value="Leather sofa upholstery">Leather sofa upholstery</option>
              <option value="Outdoor, Pool and Garden ReUpholstery">Outdoor, Pool and Garden ReUpholstery</option>
              <option value="Sofas, Chairs and Stools ReUpholstered">Sofas, Chairs and Stools ReUpholstered</option>
              <option value="Custom Partitions and Headboards">Custom Partitions and Headboards</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Control type="email" id='email' placeholder="email@example.com" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Control type="tel" id='phone' placeholder="tel: +971501234567" required />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Control type="text" id='name' placeholder="full name" required />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Control as="textarea" id='text' rows={3} placeholder='Leave a message' />
          </Form.Group>
          <Button type='submit' id='sub' >Booking Now</Button>
        </Form>
      </Modal.Body>
    </Modal>



  );
}

export default Mode;
