import React, { createContext, useState } from "react";
import Body from "./Body";
import Footer from "./Footer";
import Header from "./Header";

export const ModeContext = createContext();

export const ModeProvider = props => {
  const [fullscreen, setFullscreen] = useState(true);
  const [show, setShow] = useState(false);
  const [Language, setLanguage] = ({
    header: { title: 'Rukn Al-jazeera', service: 'Our Services', about: 'About Us', contact: 'Contact Us', language: 'Loanguage' },
    slide: {
      1: 'Beauty is that which is simultaneously attractive and sublime.',
      2: 'You can not really say what is beautiful about a place, but the image of the place will remain vividly with you.',
      3: 'Beauty is whatever gives joy.',
      4: 'A beautiful life begins at home" — thats our motto. Be mindful of the things, places and relationships that give you joy by contemplating these eloquent words of wisdom.',
      5: 'We Serve you.',
      6: 'We make you look better.',
      7: 'Let us make your place more beauty.',
      8: 'Life is for service. And we like to do that for you.'
    },
    iservice:{
      1:'Leather upholstery',
      2:'Outdoor, Pool, Garden',
      3:'Sofas, Chairs, Stools',
      4:'Partitions&Headboards'
    },
    service:{
      title:'Our Services',
      1:'Professional Upholstery/ReUpholstery service',
      2:'Leather sofa upholstery',
      3:'Outdoor, Pool and Garden ReUpholstery',
      4:'Sofas, Chairs and Stools ReUpholstered',
      5:'Sofa foam replacement',
      6:'Reupholstery Fabrics and Leather',
      7:'Custom Partitions and Headboards',
      8:'Sofa Repair',
      9:'Hotel Sofa, Chairs Upholstery/ReUpholstery',
      button:'Order The Services Now'
    },
    oldnew:{
      befor:'Befor',
      after:'After'
    },
    about:{
      title:'About Us',
      body:'Interior decoration of any place needs something more than what you have expected – mainly for the modern look and to make your lifestyle better. We at Modern Skipper Upholstery are well-aware of the importance of different things and furniture pieces required to transform any space. Keeping the same concern in mind, we have come up with the best range of furniture pieces and different things like Wooden Blinds, Roman Blinds, Roller Blinds, Vertical Blinds, Curtains and Blinds, Flooring Coverings, Sofa Upholstery and Carpets & Wooden Flooring Installation. In addition to the aforementioned things, we also offer you Fabric Sofa Upholstery, Leather Sofa Upholstery, Sofa foam replacement, Headboard Upholstery, Hospital & Clinic Curtain. Our products are made of premium quality material and by following quality standards. Choose the best range in your favorite color combination, design and style and get it delivered to your address in Dubai with installation services.',

    },
    contact:{
      title:'Contact Us',
      email:'name@example.com',
      phone:'tel: +971501234567',
      name:'Your full name'
    },
    footer:{
      title:'Subscrip Now'
    }


  });

  return (
    <ModeContext.Provider value={[show, setShow]}>
      <Header />
      <Body />
      <Footer />
    </ModeContext.Provider>
  );
};
