import { useContext } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import IServicesItem from "./IServicesItem";
import { ModeContext } from "./ModeContext";
function About() {
  const [show, setShow, language, setLanguage, mobile] =
    useContext(ModeContext);
  const sty = { textAlign: "justify", direction: language.footer.dir };
  const sty2 = { height: mobile.about };
  return (
    <div id="about" /* className="About" */ /* style={sty2} */>
      <br />

      <Container>
        <br />
        <br />
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <center>
          * Stack the columns on mobile by making one full-width and the other
          half-width */
        </center>
        <br />
        <br />

        <Row style={{ justify: "center", width: "70%" }}>
          <Col
            xs={12}
            md={6}
            style={{
              backgroundImage: 'url("./img/logo.jpeg")',
              height: "50vh",
            }}
          >
            <Button
              btn="light"
              bg="light"
              variant="light"
              style={{
                borderRadius: "0px",
                position: "absolute",
                marginBottom: "10px",
              }}
            >
              Shop Bed Linens
            </Button>
          </Col>
          <Col
            xs={12}
            md={6}
            style={{ backgroundImage: 'url("./img/1.jpeg")' }}
          ></Col>
        </Row>
        <Row>
          <Col
            xs={12}
            md={6}
            style={{
              backgroundImage: 'url("./img/2.jpg")',
              height: "50vh",
            }}
          ></Col>
          <Col
            xs={12}
            md={6}
            style={{ backgroundImage: 'url("./img/3.jpg")' }}
          ></Col>
        </Row>
      </Container>
    </div>
  );
}
export default About;
