import React,{ createContext, useState } from "react";
import Body from "./Body";
import Footer from "./Footer";
import Header from "./Header";

export const ModeContext = createContext();

export const ModeProvider = props => {
  const [fullscreen, setFullscreen] = useState(true);
  const [show, setShow] = useState(false);
  const [Language, setLanguage] = ({header:{title:'Rukn Al-jazeera', service:'Our Services', about: 'About Us', contact:'Contact Us', language:'Loanguage'},
slide:{1:'Beauty is that which is simultaneously attractive and sublime.',
2:'You can not really say what is beautiful about a place, but the image of the place will remain vividly with you.',
3:'Beauty is whatever gives joy.',
4:'A beautiful life begins at home" — thats our motto. Be mindful of the things, places and relationships that give you joy by contemplating these eloquent words of wisdom.',
5:'We Serve you.',
6:'We make you look better.',
7:'Let us make your place more beauty.',
8:'Life is for service. And we like to do that for you.'}})
  return(
    <ModeContext.Provider value={[show, setShow]}>
      <Header />
      <Body />
      <Footer />
    </ModeContext.Provider>
  );
};
